import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";

function PhotoDetail() {
  const { photoId } = useParams();
  const [photo, setPhoto] = useState(null);
  const [comment, setComment] = useState("");
  const [editingCommentId, setEditingCommentId] = useState(null);
  const [editedComment, setEditedComment] = useState("");
  const [showEditModal, setShowEditModal] = useState(false);
  const [photoDescription, setPhotoDescription] = useState("");
  const [showImageChangeModal, setShowImageChangeModal] = useState(false);
  const [newImage, setNewImage] = useState(null);
  const navigate = useNavigate();
  const user = useSelector((state) => state.user);

  const fetchPhoto = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/photo/${photoId}`,
        {
          credentials: "include",
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch photo");
      }

      const data = await response.json();
      setPhoto(data);
      setPhotoDescription(data.description || "");
    } catch (error) {
      console.error("Error fetching photo:", error);
    }
  };

  useEffect(() => {
    fetchPhoto();
  }, [photoId]);

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!comment.trim()) {
      alert("Comment cannot be empty");
      return;
    }
    try {
      const response = await fetch(
        `http://localhost:5000/api/commentsOfPhoto/${photoId}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ comment }),
          credentials: "include",
        }
      );

      if (!response.ok) {
        throw new Error("Failed to add comment");
      }

      await fetchPhoto();
      setComment("");
    } catch (error) {
      console.error("Error adding comment:", error);
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (window.confirm("Are you sure you want to delete this comment?")) {
      try {
        const response = await fetch(
          `http://localhost:5000/api/comments/${photoId}/${commentId}`,
          {
            method: "DELETE",
            credentials: "include",
          }
        );

        if (!response.ok) {
          throw new Error("Failed to delete comment");
        }

        await fetchPhoto();
      } catch (error) {
        console.error("Error deleting comment:", error);
      }
    }
  };

  const handleEditComment = (commentId, currentComment) => {
    setEditingCommentId(commentId);
    setEditedComment(currentComment);
  };

  const handleUpdateComment = async (commentId) => {
    if (!editedComment.trim()) {
      alert("Comment cannot be empty");
      return;
    }
    try {
      const response = await fetch(
        `http://localhost:5000/api/comments/${photoId}/${commentId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ comment: editedComment }),
          credentials: "include",
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update comment");
      }

      await fetchPhoto();
      setEditingCommentId(null);
      setEditedComment("");
    } catch (error) {
      console.error("Error updating comment:", error);
    }
  };

  const handleBackToHome = () => {
    navigate(`/users/${user.userId}/photos`);
  };

  const handleUpdatePhoto = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/api/photos/${photoId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ description: photoDescription }),
          credentials: "include",
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update photo");
      }

      await fetchPhoto();
      setShowEditModal(false);
      alert("Photo updated successfully!");
    } catch (error) {
      console.error("Error updating photo:", error);
      alert("Failed to update photo. Please try again.");
    }
  };

  const handleImageChange = async () => {
    if (!newImage) {
      alert("Please select a new image");
      return;
    }

    const formData = new FormData();
    formData.append("file", newImage);
    formData.append("photoId", photoId);

    try {
      const response = await fetch(
        `http://localhost:5000/api/photos/${photoId}/image`,
        {
          method: "PUT",
          body: formData,
          credentials: "include",
        }
      );

      if (!response.ok) {
        throw new Error("Failed to update image");
      }

      await fetchPhoto();
      setShowImageChangeModal(false);
      setNewImage(null);
      alert("Image updated successfully!");
    } catch (error) {
      console.error("Error updating image:", error);
      alert("Failed to update image. Please try again.");
    }
  };

  const handleDeletePhoto = async () => {
    if (window.confirm("Are you sure you want to delete this photo?")) {
      try {
        const response = await fetch(
          `http://localhost:5000/api/photos/${photoId}`,
          {
            method: "DELETE",
            credentials: "include",
          }
        );

        if (!response.ok) {
          throw new Error("Failed to delete photo");
        }

        alert("Photo deleted successfully!");
        navigate(`/users/${user.userId}/photos`);
      } catch (error) {
        console.error("Error deleting photo:", error);
        alert("Failed to delete photo. Please try again.");
      }
    }
  };

  if (!photo) return (
    <div className="container mt-5 text-center">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
      <p className="mt-2">Loading photo details...</p>
    </div>
  );

  const isPhotoOwner = photo.user_id._id === user.userId;

  return (
    <div className="container mt-4">
      <div className="navigation-header d-flex align-items-center mb-4">
        <button 
          className="btn btn-outline-primary d-flex align-items-center btn-lg"
          onClick={handleBackToHome}
        >
          <i className="bi bi-arrow-left me-2"></i> Back to Gallery
        </button>
        <h2 className="ms-3 mb-0">Photo Details</h2>
      </div>

      <div className="row">
        <div className="col-lg-8">
          <div className="card mb-4 border-0 photo-detail-card">
            <img 
              className="card-img-top photo-detail-img" 
              src={photo.file_name} 
              alt="Photo"
            />
          </div>
        </div>
        <div className="col-lg-4">
          <div className="card mb-4 border-0 photo-detail-card">
            <div className="card-body">
              <h5 className="card-title border-bottom pb-2 mb-3">About this Photo</h5>
              <p className="mb-2">
                <strong>Photographer:</strong> {photo.user_id.first_name} {photo.user_id.last_name}
              </p>
              <p className="mb-2">
                <strong>Uploaded:</strong> {new Date(photo.date_time).toLocaleString()}
              </p>
              {photo.description && (
                <div className="mt-3">
                  <strong>Description:</strong>
                  <p className="mt-1">{photo.description}</p>
                </div>
              )}

              {isPhotoOwner && (
                <div className="mt-4 d-grid gap-2">
                  <button 
                    className="btn btn-outline-primary d-flex align-items-center justify-content-center"
                    onClick={() => setShowEditModal(true)}
                  >
                    <i className="bi bi-pencil me-2"></i> Edit Description
                  </button>
                  <button 
                    className="btn btn-outline-secondary d-flex align-items-center justify-content-center"
                    onClick={() => setShowImageChangeModal(true)}
                  >
                    <i className="bi bi-image me-2"></i> Change Image
                  </button>
                  <button 
                    className="btn btn-outline-danger d-flex align-items-center justify-content-center"
                    onClick={handleDeletePhoto}
                  >
                    <i className="bi bi-trash me-2"></i> Delete Photo
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="comment-section p-4 bg-white rounded photo-detail-card">
        <h3 className="border-bottom pb-2 mb-3">Comments ({photo.comments.length})</h3>

        {photo.comments.length === 0 ? (
          <div className="text-center p-4 bg-light rounded">
            <p className="mb-0">No comments yet. Be the first to comment!</p>
          </div>
        ) : (
          <div className="list-group list-group-flush mb-4">
            {photo.comments.map((c) => (
              <div key={c._id} className="list-group-item py-3 px-0 border-bottom">
                {editingCommentId === c._id ? (
                  <div className="form-group">
                    <textarea
                      className="form-control mb-2"
                      value={editedComment}
                      onChange={(e) => setEditedComment(e.target.value)}
                    ></textarea>
                    <div className="mt-2">
                      <button 
                        className="btn btn-success btn-sm me-2"
                        onClick={() => handleUpdateComment(c._id)}
                      >
                        <i className="bi bi-check2 me-1"></i> Save
                      </button>
                      <button 
                        className="btn btn-secondary btn-sm"
                        onClick={() => setEditingCommentId(null)}
                      >
                        <i className="bi bi-x me-1"></i> Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="mb-2 comment-text">{c.comment}</div>
                    <div className="d-flex justify-content-between align-items-center">
                      <small className="text-muted">
                        <strong>{c.user_id.first_name} {c.user_id.last_name}</strong> â€¢ {new Date(c.date_time).toLocaleString()}
                      </small>
                      {c.user_id._id === user.userId && (
                        <div>
                          <button 
                            className="btn btn-link btn-sm me-2 p-0 text-primary"
                            onClick={() => handleEditComment(c._id, c.comment)}
                          >
                            <i className="bi bi-pencil"></i> Edit
                          </button>
                          <button 
                            className="btn btn-link btn-sm p-0 text-danger"
                            onClick={() => handleDeleteComment(c._id)}
                          >
                            <i className="bi bi-trash"></i> Delete
                          </button>
                        </div>
                      )}
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        )}

        <form onSubmit={handleCommentSubmit} className="mt-4">
          <div className="form-group mb-3">
            <label className="form-label"><strong>Add a Comment</strong></label>
            <textarea
              className="form-control mb-2"
              rows={3}
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Share your thoughts about this photo..."
            ></textarea>
          </div>
          <button className="btn btn-primary" type="submit">
            <i className="bi bi-chat-dots me-2"></i> Post Comment
          </button>
        </form>
      </div>

      {/* Edit Description Modal */}
      {showEditModal && (
        <div className="modal-backdrop"></div>
      )}
      {showEditModal && (
        <div className="modal">
          <div className="modal-dialog">
            <div className="modal-content photo-detail-card">
              <div className="modal-header">
                <h5 className="modal-title">Edit Photo Description</h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setShowEditModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="form-group mb-3">
                  <label className="form-label">Description</label>
                  <textarea
                    className="form-control"
                    rows={3}
                    value={photoDescription}
                    onChange={(e) => setPhotoDescription(e.target.value)}
                    placeholder="Add a description for your photo"
                  ></textarea>
                </div>
              </div>
              <div className="modal-footer">
                <button 
                  className="btn btn-secondary"
                  onClick={() => setShowEditModal(false)}
                >
                  Cancel
                </button>
                <button 
                  className="btn btn-primary"
                  onClick={handleUpdatePhoto}
                >
                  Save Changes
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Change Image Modal */}
      {showImageChangeModal && (
        <div className="modal-backdrop"></div>
      )}
      {showImageChangeModal && (
        <div className="modal">
          <div className="modal-dialog">
            <div className="modal-content photo-detail-card">
              <div className="modal-header">
                <h5 className="modal-title">Change Photo</h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setShowImageChangeModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="form-group mb-3">
                  <label className="form-label">Select New Image</label>
                  <input
                    type="file"
                    className="form-control"
                    accept="image/*"
                    onChange={(e) => setNewImage(e.target.files[0])}
                  />
                </div>
                <div className="upload-preview-container mt-3 mb-3 d-flex align-items-center justify-content-center bg-light rounded" style={{ height: "200px" }}>
                  {newImage ? (
                    <img 
                      src={URL.createObjectURL(newImage)} 
                      alt="New Preview" 
                      style={{ maxWidth: "100%", maxHeight: "100%", objectFit: "contain" }}
                    />
                  ) : (
                    <div className="text-center text-muted">
                      <i className="bi bi-image" style={{ fontSize: "2rem" }}></i>
                      <p>Preview will appear here</p>
                    </div>
                  )}
                </div>
              </div>
              <div className="modal-footer">
                <button 
                  className="btn btn-secondary"
                  onClick={() => setShowImageChangeModal(false)}
                >
                  Cancel
                </button>
                <button 
                  className="btn btn-primary"
                  onClick={handleImageChange}
                  disabled={!newImage}
                >
                  Update Image
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default PhotoDetail;
